import React, { Component } from 'react';
import "../App.css";
import Axios from 'axios';
import { Link } from 'react-router-dom';


class AddNewItem extends Component {
  state = {
    name: '',
    description: '',
    itemCost: 0,
    retailInput: 0,

  }

  updateInput = (e) => {
    this.setState({ [e.target.id]: e.target.value })
    // this.state[e.target.id] = e.target.value
    // fancy way of saying this
  }

  createANewProject = (e) => {
    e.preventDefault();
    const newName = this.state.name;
    const newDescription = this.state.description;
    const newCost = this.state.itemCost;
    const newretailInput = this.state.retailInput;
    // grab the values from the DOM

    // gotta send withCredentials: true as a header because
    // the route we are posting to uses req.user which is by default protected by express
    Axios.post("http://localhost:5000/api/items/add-new",
      { name: newName, description: newDescription , itemCost: newCost, retailPrice:newretailInput},
      { withCredentials: true })
      .then((responeFromOurAPI) => {
        console.log('success', responeFromOurAPI)

        this.props.letTheIndexComponentKnowThatWeAddedAProject();
        // after we send the axios request, we call the function in the parent component
        // to make that component go and get all the project again so now it should have the new project we just added


      })
      .catch((err) => {
        console.log('error creating task', err)
      })
  }


  render() {
    return <div>
        <h2>Add New Item</h2>
        <form onSubmit={this.createANewProject}>
          <label>Name</label>
          <input value={this.state.name} id="name" onChange={this.updateInput} />

          <label>Description</label>
          <input value={this.state.description} id="description" onChange={this.updateInput} />
          <label>Item Cost</label>
          <input type="number" value={this.state.itemCost} id="itemCost" onChange={this.updateInput} />

          <label>Retail Price</label>
          <input type="number"  value={this.state.retailInput} id="retailInput" onChange={this.updateInput} />

          <button>Save</button>
        </form>
      </div>;
  }





}


export default AddNewItem;
