import React, { Component } from 'react';
import "../App.css";
import Axios from 'axios';
import { Route, Switch, Link } from 'react-router-dom';

import AddNewItem from './AddNewItem';


class ItemIndex extends Component {
  state = {
    allTheItems: []
  };
  componentWillMount() {
    this.fetchItems();
  }

  fetchItems = () => {
    Axios.get("http://localhost:5000/api/items")
      .then(responseFromApi => {
        this.setState({ allTheItems: responseFromApi.data.reverse() });
      })
      .catch(err => {});
  };

  showAllItems = () => {
    //this.fetchItems()
    const allTheItems = this.state.allTheItems;
    // console.log(allTheItems)

    return allTheItems.map(eachItem => {
      console.log(eachItem);
      return (
        <div className="eachItem">
          <h3>item Name: {eachItem.name}</h3>
          <h6>item Description:{eachItem.description}</h6>
          <h6>item Cost: {eachItem.itemCost}</h6>
          <h6>item Retail price: {eachItem.retailPrice}</h6>
          <Link to={`/items/details/${eachItem._id}`}>See Details</Link>
        </div>
      );
    });
  };

  addItemToState = (item) => {
    console.log(item)
    let allItems = [...this.state.allTheItems];
    allItems.unshift(item.data)
    this.setState({ allTheItems: allItems });
  }

  render() {
    // console.log(this.props)

    return <div>
        <div>
          <table>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Cost</th>
              <th>Retail Price</th>
            </tr>
            <tr>
              <td>
                <h3>{this.showAllItems()}</h3>
              </td>
            </tr>
          </table>
        </div>

        {/* <div>
          {this.showAllItems()}
        </div> */}
        <div>
          <AddNewItem addItemToState={this.addItemToState} />
        </div>
      </div>;
  }
}
export default ItemIndex;
